	const TITLE_LINE_REGEX = /^(?:\/\/|==)\s*/

	// Keep the id of the plugin-event for "inkscript-integration"
	let inkscriptIntegrationEventId = undefined;

	let pageLineCounts = [];

	// Ink script from pages that aren't the currently edited one
	let nonEditedScriptBefore = "";
	let nonEditedScriptAfter = "";

	let editedPageIndex = 0;
	function setEditedPageIndex(index) {
		const pageCount = allFields_badEventOk(getInkscriptIntegrationEvent(), 'ink-script', 'text').length;
		index = Math.min(Math.max(index, 0), pageCount);
		replaceFields(CONFIG, 'edited-page-index', 'json', index);
	}

	function FIELDS_badEventOk(event, key, type) {
		if (!event) return [];
		return FIELDS(event, key, type);
	}

	function allFields_badEventOk(event, key, type) {
		if (!event) return [];
		return allFields(event, key, type);
	}

	let priorPageTitle = "";
	function refreshPagesButton(editedPageText, titleTextOnly) {
		// Current page heading
		editedPageText ??= codeMirror.getDoc();
		const newLineIndex = editedPageText.indexOf('\n');
		const pagesFirstLine = (newLineIndex === -1 ? editedPageText : editedPageText.slice(0, newLineIndex))
		const maxTitleLength = parseInt(FIELD(CONFIG, 'max-title-length', 'json'), 10) || 20;
		let pageTitle = pagesFirstLine.slice(0, maxTitleLength).trim().replace(TITLE_LINE_REGEX, '');
		if (titleTextOnly && pageTitle == priorPageTitle) return;
		priorPageTitle = pageTitle;
		if (pageTitle) {
			pageTitle = ` - ${pageTitle}`;
		}
		// Page count
		const pageCount = allFields_badEventOk(getInkscriptIntegrationEvent(), 'ink-script', 'text').length;
		// Update text
		pagesButton.innerHTML = `PAGE: ${editedPageIndex + 1}/${pageCount}${pageTitle}`;
	}

	// Call to get the plugin event for "inkscript-integration"
	function getInkscriptIntegrationEvent(checkValidity) {
		let result = null;
		if (inkscriptIntegrationEventId != undefined) {
			result = window.getEventById(window.EDITOR.stateManager.present, inkscriptIntegrationEventId);
			// Make sure we have the right event
			if (!result) {
				inkscriptIntegrationEventId = undefined;
			} else if (checkValidity && !FIELD(result, INKSCRIPT_INTEGRATION_ID_TAG, 'tag')) {
				inkscriptIntegrationEventId = undefined;
			}
		}
		// We weren't able to get a valid plugin-event, search for it
		if (inkscriptIntegrationEventId === undefined) {
			result = window.findEventByTag(window.EDITOR.stateManager.present, INKSCRIPT_INTEGRATION_ID_TAG);
			// Remember the plugin event's id for next time
			if (result) {
				inkscriptIntegrationEventId = result.id;
			}
		}
		return result;
	}

