	function refreshErrorMarksInEditor() {
		const pageErrorLines = allErrorLines?.[editedPageIndex] || new Set();
		Array.from(document.getElementsByClassName('cm-gutterElement')).forEach(ui => {
			if (pageErrorLines.has(parseInt(ui.innerHTML, 10))) {
				ui.classList.add('errorLine');
			} else {
				ui.classList.remove('errorLine');
			}
		});
	}

	let compileDelayTimer = null;
	let inkStory = null;
	async function compileInkSource() {
		// Delay until there are 2 seconds of no activity
		if (compileDelayTimer) {
			clearTimeout(compileDelayTimer);
		}
		await new Promise(resolve => { compileDelayTimer = setTimeout(resolve, 2000); });

		const editedPageText = codeMirror.getDoc();
		refreshPagesButton(editedPageText, true);
		const compiler = new inkjs.Compiler(`${nonEditedScriptBefore}${editedPageText}${nonEditedScriptAfter}`);
		try
		{
			inkStory = compiler.Compile();
			inkPlayerContainer.classList.remove('errorDisplay');
			inkStoryBuffer = '';
			historyIndex = 0;
			runInkStory();
			Array.from(document.getElementsByClassName('cm-gutterElement')).forEach(ui => {
				ui.classList.remove('errorLine');
			});
			allErrorLines = null;
		} catch (e) {
			inkPlayerContainer.classList.add('errorDisplay');
			const errorsAndWarnings = [].concat(compiler._errors, compiler._warnings);
			if (errorsAndWarnings.length > 0) {
				allErrorLines = {};
				// Update the page count for the currently edited page
				const lineBreakCount = codeMirror.getDoc().match(/\n/gi)?.length ?? 0;
				pageLineCounts[editedPageIndex] = lineBreakCount + 1;
				for (let i = 0; i < errorsAndWarnings.length; i++) {
					const msg = errorsAndWarnings[i];
					let lineNumberIndex = -1;
					if (msg.startsWith('ERROR: line ')) {
						lineNumberIndex = 12;
					} else if (msg.startsWith('WARNING: line ')) {
						lineNumberIndex = 14;
					} else {
						continue;
					}
					const absoluteLine = parseInt(msg.slice(lineNumberIndex, msg.indexOf(':', lineNumberIndex)), 10);
					let pageIndex = -1;
					let pageLine = absoluteLine;
					for (pageIndex = 0; pageIndex < pageLineCounts.length; pageIndex++) {
						if (pageLine - pageLineCounts[pageIndex] > 0) {
							pageLine -= pageLineCounts[pageIndex];
						} else {
							break;
						}
					}
					errorsAndWarnings[i] = errorsAndWarnings[i].slice(0, lineNumberIndex - 5) + `page ${pageIndex + 1} line ${pageLine}` + errorsAndWarnings[i].slice(msg.indexOf(':', lineNumberIndex));
					allErrorLines[pageIndex] ??= new Set();
					allErrorLines[pageIndex].add(pageLine);
				}
				inkPlayer.innerText = errorsAndWarnings.join('\n\n');
				refreshErrorMarksInEditor();
			} else {
				allErrorLines = null;
			}
		}
	}

	function onPlayerplayerRestartButton() {
		inkStoryBuffer = '';
		choiceHistory = [];
		historyIndex = 0;
		inkStory.ResetState();
		runInkStory();
	}
	playerRestartButton.addEventListener('click', onPlayerplayerRestartButton);

	function onPlayerRewindButton() {
		inkStoryBuffer = '';
		choiceHistory.pop();
		historyIndex = 0;
		inkStory.ResetState();
		runInkStory();
	}
	playerRewindButton.addEventListener('click', onPlayerRewindButton);

	let inkStoryBuffer = '';
	let choiceHistory = [];
	let historyIndex = 0;
	let latestContentHeight = 0;

	function onChoiceUiClicked(index) {
		inkStory.ChooseChoiceIndex(index);
		choiceHistory.push(index);
		historyIndex++;
		runInkStory();
	}

	function runInkStory() {
		let sectionAdded = false;
		while (inkStory.canContinue) {
			const nextText = inkStory.Continue().trim();
			if (nextText) {
				inkStoryBuffer += `<ink-output>${nextText}</ink-output>`;
				sectionAdded = true;
			}
		}
		if (sectionAdded) {
			inkStoryBuffer += '<hr>';
		}
		inkPlayer.innerHTML = inkStoryBuffer; 
		if (!inkStory.currentChoices.length) {
			return;;
		}
		if (historyIndex < choiceHistory.length) {
			inkStory.ChooseChoiceIndex(choiceHistory[historyIndex]);
			historyIndex++;
			runInkStory()
			return;
		}
		const contentHeight = inkPlayer.scrollHeight;
		inkStory.currentChoices.forEach((choice, index) => {
			const choiceUi = document.createElement('a');
			inkPlayer.append(choiceUi);
			choiceUi.innerText = choice.text;
			choiceUi.addEventListener('click', () => onChoiceUiClicked(index));
		});
		inkPlayerContainer.scrollTop = latestContentHeight - INK_PLAYER_SCROLL_REDUCTION;
		latestContentHeight = contentHeight;
	}

	fullscreenPlayerButton.addEventListener('click', () => toggleFullscreen(inkPlayer));
	fullscreenEditorButton.addEventListener('click', () => toggleFullscreen(inkEditor));
	function toggleFullscreen(uiElement) {
		if (!document.fullscreenElement && !document.mozFullScreenElement && !document.webkitFullscreenElement && !document.msFullscreenElement) {
			if (uiElement.requestFullscreen) {
				uiElement.requestFullscreen();
			} else if (document.documentElement.msRequestFullscreen) {
				uiElement.msRequestFullscreen();
			} else if (document.documentElement.mozRequestFullScreen) {
				uiElement.mozRequestFullScreen();
			} else if (document.documentElement.webkitRequestFullscreen) {
				uiElement.webkitRequestFullscreen(Element.ALLOW_KEYBOARD_INPUT);
			}
		} else {
			// eslint-disable-next-line no-lonely-if
			if (document.exitFullscreen) {
				document.exitFullscreen();
			} else if (document.msExitFullscreen) {
				document.msExitFullscreen();
			} else if (document.mozCancelFullScreen) {
				document.mozCancelFullScreen();
			} else if (document.webkitExitFullscreen) {
				document.webkitExitFullscreen();
			}
		}
	}

	(function () {
		const VERSION = 1;
		if (window.popup && window.popup.version >= VERSION) return;
		window.popup = { version: VERSION };
		// Root
		const container = document.createElement('div');
		document.getElementById('editor').append(container);
		container.id = 'suboptions';
		container.setAttribute('style', 'position:fixed;z-index:100;display:none;');
		// Dimmer
		const dimmer = document.createElement('div');
		container.append(dimmer);
		dimmer.setAttribute('style', 'position:fixed;background-color:black;opacity:60%;left:0;top:0;right:0;bottom:0;');
		// Dialog ui
		window.popup.uiDialog = document.createElement('div');
		container.append(window.popup.uiDialog);
		window.popup.uiDialog.setAttribute('style', 'background-color:var(--frame-color);border-radius:1rem;padding:1rem;position:fixed;width:490px;top:50%;left:50%;margin:-200px -245px;');
		// Title
		window.popup.uiTitle = document.createElement('div');
		window.popup.uiDialog.append(window.popup.uiTitle);
		window.popup.uiTitle.setAttribute('style', 'text-align:center;margin-bottom:.5rem;font-size:125%;');
		// Select list - defined first as a "select-or-enter" popup is possible using both this AND text input.
		window.popup.uiSelectList = document.createElement('div');
		window.popup.uiDialog.append(window.popup.uiSelectList);
		window.popup.uiSelectList.setAttribute('style', 'display: flex;gap:0.75rem;');
		window.popup.uiSelectListItems = document.createElement('select');
		window.popup.uiSelectList.append(window.popup.uiSelectListItems);
		window.popup.uiSelectListItems.setAttribute('style', 'margin-bottom:1rem;');
		window.popup.uiSelectListItems.setAttribute('size', 10);
		window.popup.uiSelectListDescription = document.createElement('div');
		window.popup.uiSelectList.append(window.popup.uiSelectListDescription);
		window.popup.uiSelectListDescription.setAttribute('style', 'display:inline-block;border:1px solid black;margin-bottom:1em;flex:1;padding: 0.5rem;border-radius:1rem;');
		window.popup.uiSelectListItems.addEventListener('change', () => {
			window.popup.uiSelectListDescription.innerHTML = window.popup.uiSelectListItems.selectedOptions[0].dataset.description.replaceAll('\n', '<br/>');
		});
		// Text input (single-line)
		window.popup.uiTextInput = document.createElement('input');
		window.popup.uiDialog.append(window.popup.uiTextInput);
		window.popup.uiTextInput.setAttribute('style', 'margin-bottom:.75rem;width:100%;outline:none;');
		window.popup.uiTextInput.setAttribute('type', 'text');
		// Text input (multi-line)
		window.popup.uiTextarea = document.createElement('textarea');
		window.popup.uiDialog.append(window.popup.uiTextarea);
		window.popup.uiTextarea.setAttribute('style', 'margin-bottom:.75rem;width:100%;outline:none;resize:none;');
		// Options
		window.popup.uiOptions = document.createElement('div');
		window.popup.uiDialog.append(window.popup.uiOptions);
		window.popup.uiOptions.setAttribute('style', 'display:flex;flex-direction:row;flex-wrap:wrap;gap:0.5rem;display:none;');
		// Methods
		window.popup.addOptionButton = (name, action) => {
			const button = document.createElement('button');
			window.popup.uiOptions.append(button);
			button.style.padding = '.5em .7em';
			button.innerText = name;
			button.addEventListener('click', () => {
				window.popup.hide();
				action();
			});
			return button;
		};
		window.popup.prepare = (title) => {
			window.popup.uiTitle.innerHTML = title.replaceAll('\n', '<br/>');
			// All ui defaults to hidden
			window.popup.uiSelectList.style.display = 'none';
			window.popup.uiTextInput.style.display = 'none';
			window.popup.uiTextarea.style.display = 'none';
			window.popup.uiOptions.style.display = 'flex';
		};
		window.popup.show = (okButtonText, cancelButtonText, onClose) => {
			if (okButtonText || cancelButtonText) {
				window.popup.uiOptions.innerHTML = '';
				window.popup.uiOptions.style.display = 'flex';
			}
			const result = [];
			if (okButtonText) {
				result.push(window.popup.addOptionButton(okButtonText, () => onClose(true)));
			}
			if (cancelButtonText) {
				result.push(window.popup.addOptionButton(cancelButtonText, () => onClose(false)));
			}
			container.style.display = 'block';
			return result;
		};
		window.popup.hide = () => {
			container.style.display = 'none';
		};

		window.popup.showAlert = message => {
			return new Promise(resolve => {
				window.popup.prepare(message);
				window.popup.show('Ok', null, result => resolve(result));
			});
		};
		window.popup.showConfirm = message => {
			return new Promise(resolve => {
				window.popup.prepare(message);
				window.popup.show('Ok', 'Cancel', result => resolve(result));
			});
		};
		window.popup.showBinaryChoice = (message, trueName, falseName) => {
			return new Promise(resolve => {
				window.popup.prepare(message);
				window.popup.show(trueName, falseName, result => resolve(result));
			});
		};
		window.popup.showPrompt = (title, defaultText, multiline) => {
			return new Promise(resolve => {
				window.popup.prepare(title);
				const inputUi = multiline ? window.popup.uiTextarea : window.popup.uiTextInput;
				inputUi.value = defaultText || '';
				inputUi.style.display = 'block';
				window.popup.show('Ok', 'Cancel', isAccepted => resolve(isAccepted ? inputUi.value : null));
			});
		};
		window.popup.showSelectList = (title, items, allowMultiselect) => {
			return new Promise(resolve => {
				window.popup.prepare(title);

				// If there are descriptions, show the descriptions ui
				let hideDescription = true;
				items.forEach(value => {
					if (value.description) {
						hideDescription = false;
					}
				});
				window.popup.uiSelectListDescription.style.display = hideDescription ? 'none' : 'inline-block';
				window.popup.uiSelectListItems.style.width = hideDescription ? '100%' : 'auto';

				window.popup.uiSelectListItems.multiple = allowMultiselect;
				window.popup.uiSelectListItems.innerHTML = '';
				let i = 0;
				items.forEach((value, key) => {
					window.popup.uiSelectListItems[i] = new Option(key, i);
					window.popup.uiSelectListItems[i].dataset.description = value.description || '';
					i++;
				});
				window.popup.uiSelectListItems.value = 0;
				window.popup.uiSelectListItems.dispatchEvent(new Event('change'));
				window.popup.uiSelectList.style.display = 'flex';

				function getSelection() {
					const values = Array.from(items.values());
					const result = [];
					if (!allowMultiselect) {
						result.push(values[window.popup.uiSelectListItems.value]);
					} else {
						Array.from(window.popup.uiSelectListItems.selectedOptions).forEach(option => {
							result.push(values[option.value]);
						});
					}
					return result;
				}
				window.popup.show('Ok', 'Cancel', isAccepted => resolve(isAccepted ? getSelection() : null));
			});
		};
		window.popup.showOptions = (title, actions, noCancelButton) => {
			console.assert(actions instanceof Map);
			if (actions.size === 0) return;
			if (actions.size === 1) {
				// Auto-run the single action (no ui needed)
				actions.values().next().value();
				return;
			}
			window.popup.prepare(title);
			window.popup.uiOptions.innerHTML = '';
			actions.forEach((value, key) => {
				window.popup.addOptionButton(key, value);
			});
			if (!noCancelButton) {
				window.popup.addOptionButton('Cancel', () => window.popup.hide);
			}
			window.popup.uiOptions.style.display = 'flex';
			window.popup.show();
		};
	})();

